package com.feuji.commonmodel;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import java.util.List;@Entity@Table(name = "users")public class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String name;    private String email;    private String password;    @Column(name = "phno")    private String phoneNumber;    @Enumerated(EnumType.STRING)    private Role role;    @JsonIgnore    @OneToMany(mappedBy = "user", cascade = CascadeType.ALL, orphanRemoval = true)    private List<Marks> marksList;        @JsonIgnore    @OneToMany(mappedBy = "user", cascade = CascadeType.ALL)    private List<UserAnswers> userAnswersList;        public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getPhoneNumber() {        return phoneNumber;    }    public void setPhoneNumber(String phoneNumber) {        this.phoneNumber = phoneNumber;    }    public Role getRole() {        return role;    }    public void setRole(Role role) {        this.role = role;    }    public List<Marks> getMarksList() {        return marksList;    }    public void setMarksList(List<Marks> marksList) {        this.marksList = marksList;    }    public User(String name, String email, String password, String phoneNumber, Role role, List<Marks> marksList) {        this.name = name;        this.email = email;        this.password = password;        this.phoneNumber = phoneNumber;        this.role = role;        this.marksList = marksList;    }    public User() {    }}